mapper.select:
select

from works_info
where author_id = #{authorId,jdbcType=VARCHAR}
and activity_id = #{activityId,jdbcType=VARCHAR}
and delete_tag = '0'
order by create_time desc

select SUM (num_of_clicks)
from works_info
where delete_tag = '0'
and activity_id = #{activityId,jdbcType=VARCHAR}
and STR_TO_DATE(create_time,"%Y-%m-%d") = #{currentDate, jdbcType = VARCHAR}
